import * as model from './model'
import { state as STATE} from './model'
import productView from './views/productView';

// SELECT ALL ELEMENTS
const closeButton = document.querySelector('#close');
const cartButton = document.querySelector('#cart-button');
const shopButton = document.querySelector('#shop__now');
const cartOverlay = document.querySelector('#cart');
const cartMenu = document.querySelector('#cart__menu');
const productsSection = document.querySelector('#products');
const products = document.querySelectorAll('#product');
const buttons = document.querySelectorAll('.product__add-button');

// FUNCTIONS

    const hideCart = (e) => {
        e.preventDefault()
        cartMenu.classList.remove('slide-in');
        cartMenu.classList.add('slide-out');
        setTimeout( () => {
            cartOverlay.classList.remove('fade-in');
            cartOverlay.classList.add('fade-out');
        } , 300); 
    }
    
    const showCart = (e) => {
        e.preventDefault();
        cartOverlay.classList.add('fade-in');
        cartOverlay.classList.remove('fade-out');
        setTimeout( () => {
            cartMenu.classList.add('slide-in');
            cartMenu.classList.remove('slide-out');
        } , 200); 
    }

    const changeText = (e) => {
        e.currentTarget.innerHTML = 'IN CART';
    }


    const state = {
        products: []
    }

    const getData = async function(){
        try{
            const products = await [];
            const res = await fetch('https://mocki.io/v1/86fb6a73-e288-41f9-83d9-af0c5470b223');
            const data = await res.json();
            await data.forEach( product => {
                products.push(product);
            });

            console.log(typeof(data));
     return products;
        } catch(error){
            console.log(error);
        }
    }

    const init = async () => {
        const products = await getData();
        console.log(products);
        products.forEach( product => {
            productView.addItemToParent(product);
        });
    }
// EVENT LISTENERS

closeButton.addEventListener( 'click', hideCart);
cartButton.addEventListener( 'click', showCart);
buttons.forEach(button => button.addEventListener('click', changeText));
shopButton.addEventListener( 'click', () => {
    productsSection.scrollIntoView({behavior: 'smooth'});
})
products.forEach( product => {
    product.addEventListener( "mouseover", () =>{
        const button = product.querySelector('.product__add-button');
        button.classList.add('button__show');
    })

    product.addEventListener( "mouseout", () =>{
        const button = product.querySelector('.product__add-button');
        button.classList.remove('button__show');
    })
});

// window.addEventListener( 'load', init);
// init();

model.getData();
const elem = () => {
    console.log(STATE.products.length);
    model.state.products.forEach( (item, index) => {
        console.log(model.state.products[index]);
    })
    console.log("done");
}
elem();
